import React, { Component } from 'react';
import { Button, Form, FormGroup, Label, Input, FormText } from "reactstrap";
import Frame from "./components/frame";
import ContentDetails from "./components/ContentDetails";
import ItemTeaser from "./components/ItemTeaser.js";

import './App.scss';
import "./components/frame.scss";
import "./scss/ItemTeaser.scss";

import cross from "./img/cross.svg";

import data from "./data/Data";

require("typeface-montserrat");
// import { throws } from 'assert';


class App extends Component {
  constructor(props) {
    super(props);
    this.articleClose = this.articleClose.bind(this);
    this.articleOpen = this.articleOpen.bind(this);
    this.state = {
      articles: [],
      gridVisible: true,
      frameVisible: false
    };
  }

  articleOpen(item) {
    this.setState(
      {
        gridVisible: false,
        item: item
      },
      () => {
        setTimeout(() => {
          this.setState({ 
            frameVisible: true 
          });
        }, 500);
      }
    );
  }

  articleClose() {
    this.setState(
      {
        frameVisible: false,
      },
      () => {
        setTimeout(() => {
          this.setState({
            gridVisible: true
          });
        }, 500);
      }
    );
  }

  componentDidMount() {
    this.fetchData();
  }

  fetchData() {
    fetch("http://web.tcch.ch/tv-test/") //     // https://www.letemps.ch/tv-shows
      .then(response => response.json())
      .then(json => {
        this.setState({
          articles: json
        });
      })
      .catch(function() {
        console.log("Error when loading json");
      });
  }

  render() {
    const { articles } = this.state;
    const { frameVisible } = this.state;
    const { gridVisible } = this.state;

    return <div className="App">
        <aside>
          {/* <div>
            <MovieCards
              data={this.state.data}
              showData={this.seeArticle}
              numberOfCards='one'
              additionalClasses='stackable stuff yolo'
            />
          </div> */}
        </aside>
        <main>
          <div class="main-header">
          <Button color="danger">Danger!</Button><Form>
            <FormGroup>
              <Label for="exampleEmail">Email</Label>
              <Input type="email" name="email" id="exampleEmail" placeholder="with a placeholder" />
            </FormGroup>
            <FormGroup>
              <Label for="examplePassword">Password</Label>
              <Input type="password" name="password" id="examplePassword" placeholder="password placeholder" />
            </FormGroup>
            <FormGroup>
              <Label for="exampleSelect">Select</Label>
              <Input type="select" name="select" id="exampleSelect">
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </Input>
            </FormGroup>
            <FormGroup>
              <Label for="exampleSelectMulti">Select Multiple</Label>
              <Input type="select" name="selectMulti" id="exampleSelectMulti" multiple>
                <option>1</option>
                <option>2</option>
                <option>3</option>
                <option>4</option>
                <option>5</option>
              </Input>
            </FormGroup>
            <FormGroup>
              <Label for="exampleText">Text Area</Label>
              <Input type="textarea" name="text" id="exampleText" />
            </FormGroup>
            <FormGroup>
              <Label for="exampleFile">File</Label>
              <Input type="file" name="file" id="exampleFile" />
              <FormText color="muted">
                This is some placeholder block-level help text for the above input.
                It's a bit lighter and easily wraps to a new line.
          </FormText>
            </FormGroup>
            <FormGroup tag="fieldset">
              <legend>Radio Buttons</legend>
              <FormGroup check>
                <Label check>
                  <Input type="radio" name="radio1" />{' '}
                  Option one is this and thatâ€”be sure to include why it's great
            </Label>
              </FormGroup>
              <FormGroup check>
                <Label check>
                  <Input type="radio" name="radio1" />{' '}
                  Option two can be something else and selecting it will deselect option one
            </Label>
              </FormGroup>
              <FormGroup check disabled>
                <Label check>
                  <Input type="radio" name="radio1" disabled />{' '}
                  Option three is disabled
            </Label>
              </FormGroup>
            </FormGroup>
            <FormGroup check>
              <Label check>
                <Input type="checkbox" />{' '}
                Check me out
          </Label>
            </FormGroup>
            <Button>Submit</Button>
          </Form>
          </div>
          <div className={`grid ${gridVisible ? "is-visible" : ""}`}>
            {articles.length > 0 ? articles.map((item, index) => {
                  return <ItemTeaser key={index} item={item} articleOpen={this.articleOpen} />;
                }) : null}
          </div>
        </main>

        <Frame frameVisible={frameVisible}>
          <p>ici un text</p>
          <ContentDetails articleClose={this.articleClose} item={this.state.item} />
        </Frame>
      </div>;
  }
}

export default App;
