import React, { Component } from "react";
import Select from "react-select";


class FilterSelect extends Component {
  state = {
    selectedOption: null,
    articles: this.props.articles,
    articlesFiltered: null
  };

  handleChange = (selectedOption, articles, articlesFiltered) => {

    console.log(`Option selected:`, selectedOption.value);
      //this.setState({ articlesFiltered: articles.filter(item => (item.lt_tv_show_genre === selectedOption.value))});

    const monkiki = this.state.articles.filter(article => (article.lt_tv_show_genre === selectedOption.value));

    // var monkiki = articles.filter(function (article) {
    //     return article.lt_tv_show_genre == 'Historique';
    // });

    console.log(monkiki);

    this.setState({ selectedOption: selectedOption, articlesFiltered: monkiki });


    //console.log(`articlesFiltered`, articlesFiltered.length);
  };

  render() {
    const { selectedOption, articlesFiltered } = this.state;

    const options = [
      { value: "Comédie", label: "Comédie" },
      { value: "Société", label: "Société" },
      { value: "Sentimental", label: "Sentimental" },
      { value: "Policier", label: "Policier" },
      { value: "Fantastique", label: "Fantastique" },
      { value: "Historique", label: "Historique" },
      { value: "Guerre", label: "Guerre" },
      { value: "Action", label: "Action" },
      { value: "Western", label: "Western" }
    ];

    return (
      <Select options={options} value={selectedOption} onChange={this.handleChange} />
    );
  }
}



export default FilterSelect;
