import React, { Component } from 'react';
import './App.scss';
import "./components/frame.scss";
import MovieCards from "./components/movieCards.js";
import Frame from "./components/frame";
import data from "./data/Data";
import SelectCurrency from "./components/SelectCurrency.js"
import ItemsThumbs from "./components/ItemsThumbs.js"
// import { throws } from 'assert';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      // isVisible: false, // DEBUG / false
      // article: null
      items: [],
      currencies: data.currencies,
      currencyA: data.currencies[0],
      currencyB: data.currencies[1],
      currencyAval: data.currencies[0].sellRate,
      currencyBval: data.currencies[1].sellRate
    };
    // this.showFrame = this.showFrame.bind(this);
    // this.seeArticle = this.seeArticle.bind(this);
  }

  fetchData() {

    this.setState({
      //isLoading: true,
      items: []
    })

    fetch("http://web.tcch.ch/tv-test/")
      .then(response => response.json()) //turn response into json
      .then(parsedJSON => parsedJSON.results.map(dataItem => ( //parse le json
        {
          name: `${dataItem.title} `,
        }
      )))
      .then(items => this.setState({
        items
      }))
      .catch(error => console.log('parsing failed', error));
  }









  onSelectCurrency(){
    console.log('fuck');
  }

  // componentDidMount() {
  //   this.setState({ isLoading: true });
  //   fetch('http://web.tcch.ch/tv-test/') //     // https://www.letemps.ch/tv-shows
  //   .then( (response) => {
  //     return response.json() })
  //   .then( (json) => {
  //     this.setState({data: json, isLoading: false});
  //   }).catch(function() {
  //       console.log("Error when loading json");
  //   });
  // };

  // seeArticle(article) {
  //   this.setState({ article: article });
  //   this.showFrame()
  // }


  showFrame() {
    this.setState((state, props) => ({
      isVisible: true
    }));
  }

  hideFrame() {
    this.setState((state, props) => ({
      isVisible: false
    }));
  }

  render() {
    const { currencies } = this.state;
    return (
      <div className="App">
        <aside>
          {/* <div>
            <MovieCards
              data={this.state.data}
              showData={this.seeArticle}
              numberOfCards='one'
              additionalClasses='stackable stuff yolo'
            />
          </div> */}
        </aside>
        <main>
          <div className="main-header" />
          {/* <div class="main-content">
          </div> */}
        </main>
        <ItemsThumbs items={this.fetchData()}/> 
        <SelectCurrency currencies={currencies} />
        {/* <Frame isVisible={this.state.isVisible} article={this.state.article} /> */}
      </div>
    );
  }
}

export default App;
