import React, { Component } from "react";
import Select from "react-select";


class FilterSelect extends Component {

    state = {
        selectedOption: null, 
        articles: this.props.articles,
        articlesFiltered: null
    }

    handleChange = (selectedOption) => {
        this.setState({ selectedOption });
        console.log(`Option selected:`, selectedOption.value);
        console.log(`articlesFiltered`, articlesFiltered.length);
    }

  render() {

    const { selectedOption, articles, articlesFiltered  } = this.state;

    const options = [
        { value: "Comédie", label: "Comédie" },
        { value: "Société", label: "Société" },
        { value: "Sentimental", label: "Sentimental" },
        { value: "Policier", label: "Policier" },
        { value: "Fantastique", label: "Fantastique" },
        { value: "Historique", label: "Historique" },
        { value: "Guerre", label: "Guerre" },
        { value: "Action", label: "Action" },
        { value: "Western", label: "Western" }

    ];

      return <Select  options={options} value={selectedOption} onChange={this.handleChange} />;
  }
}



export default FilterSelect;
