import React, { Component } from 'react';
import './App.scss';
import "./components/frame.scss";
import MovieCards from "./components/movieCards.js";
import Frame from "./components/frame";
import ContentDetails from "./components/ContentDetails";
import data from "./data/Data";
import SelectCurrency from "./components/SelectCurrency.js"
import ItemTeaser from "./components/ItemTeaser.js"
import "./scss/ItemTeaser.scss";
require("typeface-montserrat");
// import { throws } from 'assert';


class App extends Component {
  constructor(props) {
    super(props);
    this.frameClose = this.frameClose.bind(this);
    this.frameOpen = this.frameOpen.bind(this);
    this.state = {
      articles: [],
      gridVisible: true,
      frameVisible: false
    };
  }

  frameOpen(item) {
    // this.setState({
    //   frameVisible: true,
    //   gridVisible: false,
    //   item: item
    // });

    this.setState(
      {
        gridVisible: false
      },
      () => {
        this.afterSetStateFinished();
      }
    );
  }

  afterSetStateFinished(){
    alert('coucou');
  }

  frameClose() {
    this.setState({
      frameVisible: false,
      gridVisible: true
    });
  }

  componentDidMount() {
    this.fetchData();
  }

  fetchData() {
    fetch("http://web.tcch.ch/tv-test/") //     // https://www.letemps.ch/tv-shows
      .then(response => response.json())
      .then(json => {
        this.setState({
          articles: json
        });
      })
      .catch(function() {
        console.log("Error when loading json");
      });
  }

  render() {
    const { articles } = this.state;
    const { frameVisible } = this.state;
    const { gridVisible } = this.state;

    return (
      <div className="App">
        <aside>
          {/* <div>
            <MovieCards
              data={this.state.data}
              showData={this.seeArticle}
              numberOfCards='one'
              additionalClasses='stackable stuff yolo'
            />
          </div> */}

          {/* {
            console.log(this.refs.Grid)
          } */}
        </aside>
        <main>
          <div className={`grid ${gridVisible ? "is-visible" : ""}`} ref="Grid">
            {articles.length > 0
              ? articles.map((item, index) => {
                  // const title = item.title;
                  // const image = item.np8_main_media;
                  return (
                    <ItemTeaser
                      key={index}
                      item={item}
                      frameOpen={this.frameOpen}
                    />
                  );
                })
              : null}
          </div>
        </main>

        {/* <Frame isVisible={this.state.isVisible} article={this.state.article}></Frame> */}
        <Frame frameVisible={frameVisible}>
          <p>ici un text</p>
          <ContentDetails frameClose={this.frameClose} item={this.state.item} />
        </Frame>
      </div>
    );
  }
}

export default App;
